INSERT INTO products (name, description, mainImage, stock, isHidden, isDeleted, creationDate, reviewsCount, reviewsStars) VALUES ("Lot de 4 doublures inserts bambou pour couches lavables","Lot de 4 doublures bambou pour couches lavables. Elles conviennent pour augmenter l’absorption des couches lavables. L’éponge de bambou vous garantit souplesse, finesse ainsi qu’une bonne absorption. Elles s’adaptent à la plupart de couches lavables.  Composition : 3 épaisseurs d’éponge de bambou (80% viscose de bambou 20% polyester). Dimensions : 33 cm x 14 cm. Lavage : Jusqu’à 60 ° , sèche linge autorisé.  Confectionnées en France, dans notre atelier et Tissus certifiés Oeko-Tex Classe 100 (absence de substances allergisantes ou toxiques pour la peau de bébé).","lot_de_4_doublures_bambou_pour_couches_lavables.jpg","25",0,0, NOW(), 0, 0);

INSERT INTO products (name, description, mainImage, stock, isHidden, isDeleted, creationDate, reviewsCount, reviewsStars) VALUES ("Lot de 4 voiles en micro polaire pour couches lavables","Lot de 4 voiles en micro polaire pour assurer un effet ’bébé au sec’. La peau de bébé est ainsi plus isolée de l’humidité. Ce voile sera placé dans le fond de la couche pour isoler davantage les fesses de bébé de l’humidité. Attention il n’augmente pas l’absorption de la couche lavable. Lorsque les selles de bébé seront plus moulées, le voile en micro-polaire peut remplacer les voiles de protection jetables. Il suffira alors de le secouer au dessus des toilettes pour éliminer les selles. Ce voile sèche rapidement. Composition : Micro-polaire 100 % polyester. Dimensions : 33 cm x 13 cm Lavage : Jusqu’à 60 ° Fabriqué en France, dans notre atelier et Tissus certifiés Oeko-Tex Classe 100 (absence de substances allergisantes ou toxiques pour la peau de bébé). ","lot_de_4_voiles_en_micro-polaire_pour_couche_lavable.jpg","39",0,0, NOW(), 0, 0);

